import React ,{useState} from 'react';
import Base from '../core/Base';
import {Link } from 'react-router-dom'
import {getCategories} from '.helper/adminapicall'



const AddProduct=()=>{
   
    const [values,setValues] =useState({
        name:"",
        description:"",
        price:"",
        stock:"",
        photo:"",
        error:"",
        getRedirect:false,
        formData:"",
        category:"",
        categories:[],
        loading:false,
        createdCategory:""

    });
   
    const { 
            name,description,price,stock,photo,error,getRedirect,formData,category,
            categories,loading,createdCategory
           }= values;

    const preload =()=>{
        getCategories().then(data=>{
            if(data.error){
                setValues({...values,error:error.data})
            }
            else{
                setValues({...values, categories:data})
                console.log(categories);
            }
        })
    }       
    const handleChange=()=>{
 
    }

    const onSubmit=()=>{

    }

    const createProductForm = () => (
        <form >
          <span>Post photo</span>
          <div className="form-group">
            <label className="btn btn-block btn-success">
              <input
                onChange={handleChange("photo")}
                type="file"
                name="photo"
                accept="image"
                placeholder="choose a file"
              />
            </label>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("name")}
              name="photo"
              className="form-control"
              placeholder="Name"
              value={name}
            />
          </div>
          <div className="form-group">
            <textarea
              onChange={handleChange("description")}
              name="photo"
              className="form-control"
              placeholder="Description"
              value={description}
            />
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("price")}
              type="number"
              className="form-control"
              placeholder="Price"
              value={price}
            />
          </div>
          <div className="form-group">
            <select
              onChange={handleChange("category")}
              className="form-control"
              placeholder="Category"
            >
              <option>Select</option>
              <option value="a">a</option>
              <option value="b">b</option>
            </select>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("quantity")}
              type="number"
              className="form-control"
              placeholder="Quantity"
              value={stock}
            />
          </div>
          
          <button type="submit" onClick={onSubmit} className="btn btn-outline-success">
            Create Product
          </button>
        </form>
      );



    return(
        <Base title="Add Product" description="add product" className="container bg-info p-4">
         <Link to="/admin/dashboard" className="btn btn-md btn-dark mb-4"> Admin Home</Link>
         <div className="row bg-dark text-white rounded">
         <div className="col-md-8 offset-md-2 mb-3 mt-3">
         
          {createProductForm ()}
         </div>
         </div>
        </Base>
    )
}

export default AddProduct;
