import React ,{useState,useEffect}from 'react';
import Base from '../core/Base'

import {API } from "../backend"
import Card from './card';
import { getAllProducts } from './helper/coreapicalls';
import { loadCart } from './helper/cardHelper';


export default function Cart(){
    
    const [products, setProducts] = useState([])
    
     useEffect(() => {
        setProducts(loadCart())
     }, [])

    const loadAllProucts =()=>{
        
        
        return (
            <div >
            <h2>load products</h2>
           
            
            
            {products.map((product,index)=>{
               
              return (
                <Card 
                key={index}
                product={product}
                addtocart={false}
                removefromCart={true} />
                
              )}

            )}
              
            
            </div>
        )
    }

    const loadCheckout =()=>{
        return (
            <div>
            <h2>checkout</h2></div>
        )
    }
    return (
        <Base title="Cart Page" description="Checkout">
        <div className="row">
        <div className="col-4 text-center">{loadAllProucts()}</div>
        <div className="col-4 col-offset-2 text-center">{loadAllProucts()}</div>

        
        </div>
      </Base>
  
    )
}
