import React, { useState } from "react";
import Base from "../core/Base";
import { Route, Link } from "react-router-dom";
import {  signup } from "../auth/helper";

const SignUp = () => {
    
        const [values,setValues] =useState({
            name:"",
            email:"",
            password:"",
            error:"",
            success:false
        })
    
    const {name,passowrd,email,error,success}=values;

    const handleChange =name=>event=>{
        setValues({...values,error:false,[name]:event.target.value})
    }
    
    const onSubmit =event=>{
        event.preventDefault()
        setValues({...values,error:false})
        signup({name,email,passowrd})
        .then(data=>{
            if(data.error){
                setValues({...values ,error:data.error,success:false})
            }
            else{
                setValues({...values,error:"",name:"",passowrd:"",success:false})

            }
        })
        .catch(console.log("error in sign up"))
    }



    const SignUpForm = () => {
        return (
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <form>
                        <div className='form-group'>
                            <label className="text-light">User name</label>
                            <input className='form-control' onChange={handleChange("name")} type="text"></input>
                        </div>
                        <div className='form-group'>
                            <label className="text-light">Email</label>
                            <input className='form-control'onChange={handleChange("email")} type="text"></input>
                        </div>
                        <div className='form-group'>
                            <label className="text-light">password</label>
                            <input className='form-control'onChange={handleChange("password")} type="password"></input>
                        </div>
                        <button className="btn btn-success btn-block " >Submit</button>
                    </form>
                </div>

            </div>

        )
    }

    return (
        <Base title="Sign up" description="Signup  for user">
            {SignUpForm()}
            <p className="text-white text-center">{JSON.stringify(values)}</p>
        </Base>
    );
};

export default SignUp;
