import React ,{useState,useEffect} from 'react';
import Base from '../core/Base';
import {Link } from 'react-router-dom'
import {getCategories, createProduct} from './helper/adminapicall'
import { isAuthenticated } from '../auth/helper';



const AddProduct=()=>{
    
const {user,token}= isAuthenticated();
    const [values,setValues] =useState({
        name:"",
        description:"",
        price:"",
        stock:"",
        photo:"",
        error:"",
        getRedirect:false,
        formData:"",
        category:"",
        categories:[],
        loading:false,
        createdProduct:""

    });
   
    const { 
            name,description,price,stock,photo,error,getRedirect,formData,category,
            categories,loading,createdProduct
           }= values;

    const preload =()=>{
        getCategories().then(data=>{
           
            if(data.error){
                setValues({...values,error:error.data})
            }
            else{
                setValues({...values, categories:data, formData:new FormData()})
                console.log(categories.name);
            }
        }).catch(err=>console.log(err))
    }  
    
    useEffect(() => {
        preload()
       
    }, [])

    const handleChange =name=>event=>{
        const value= name===photo?event.target.file[0]:event.target.value
        formData.set(name,value)
        setValues({...values,[name]:value})
    }

    const onSubmit=(event)=>{
        event.preventDefault();
        setValues({...values,error:"",loading:true});
        createProduct(user._id,token,formData).then(data=>{
            if(data.error){
                setValues({...values,error:data.error})
            }
            else{
                setValues({...values,
                    name:"",
                    description:"",
                    price:"",
                    stock:"",
                    photo:"",
                    losding:false,
                    createdProduct:data.name})
            }
        })

    }

    const successMessage=()=>(
        <div className="alert alert-success mt-3" style={{display : createdProduct?"":"none"}}>
        <h4>{createdProduct} Created </h4>
        </div>
           
    
       
    )

    const warningMessage=()=>{
        if(error){
            return <h4 className="text-warning">Adding Product failed !!!! </h4>
        }
       
    }

    const createProductForm = () => (
        <form >
          <span>Post photo</span>
          <div className="form-group">
            <label className="btn btn-block btn-success">
              <input
                onChange={handleChange("photo")}
                type="file"
                name="photo"
                accept="image"
                placeholder="choose a file"
              />
            </label>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("name")}
              name="photo"
              className="form-control"
              placeholder="Name"
              value={name}
            />
          </div>
          <div className="form-group">
            <textarea
              onChange={handleChange("description")}
              name="photo"
              className="form-control"
              placeholder="Description"
              value={description}
            />
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("price")}
              type="number"
              className="form-control"
              placeholder="Price"
              value={price}
            />
          </div>
          <div className="form-group">
            <select
              onChange={handleChange("category")}
              className="form-control"
              placeholder="Category"
            >
              <option>Select</option>
              {
                  categories && categories.map((cate,index)=>(
                      <option key={index} value={cate._id}>{cate.name}</option>
                  ))
              }
            </select>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("quantity")}
              type="number"
              className="form-control"
              placeholder="Quantity"
              value={stock}
            />
          </div>
          
          <button type="submit" onClick={onSubmit} className="btn btn-outline-success">
            Create Product
          </button>
        </form>
      );



    return(
        <Base title="Add Product" description="add product" className="container bg-info p-4">
         <Link to="/admin/dashboard" className="btn btn-md btn-dark mb-4"> Admin Home</Link>
         <div className="row bg-dark text-white rounded">
         <div className="col-md-8 offset-md-2 mb-3 mt-3">
         {successMessage()}
         {warningMessage()}
          {createProductForm ()}
         </div>
         </div>
        </Base>
    )
}

export default AddProduct;
