import React, { useState } from 'react';
import Base from '../core/Base';
import { isAuthenticated } from '../auth/helper';
import {Link} from 'react-router-dom'
import {addCategory} from './helper/adminapicall'

const AddCategory=()=>{

    const [name, setName] = useState("")
    const [error, setError] = useState(false) 
    const [success, setSuccess] = useState(false)
    const {user,token} =isAuthenticated();
    
    const handleChange= event =>{
         setError('');
         setName(event.target.value) 
    }
     const onSubmit =(event)=>{
         event.preventDefault();
         setError('');
         setSuccess(false);
         addCategory(user._id,token,{name})
         .then(data=>{
             if(data.error){
                 setError(data.error)
             }
             else{
                 setError('')
                 setSuccess(true)
             }
         })

     }

    const myCategoryForm =()=>(
        <form>
        <div className="form-group">
        <p className="lead" >Enter Category</p>
        <input type="text"
        placeholder="Eg summer"
        required autoFocus
        value={name}
        onChange={ handleChange}
        className="form-control my-3"></input>
        <button onClick={onSubmit} className="btn btn-outline-info">Add Category</button>
        </div>
        </form>
    )
    const goBack= ()=>(
        <div className="mt-5">
        <Link className="btn btn-sm btn-success mb-3" to="/admin/dashboard">Admin Home</Link>
        </div>
    )
    return(
        <Base className="container p-4 bg-info" 
        title="Create Category Here" description="Add New category">
        <div className="row bg-white rounded">
        <div className="col-md-8 offset-md-2">
        {myCategoryForm()}{goBack()}
        </div>
        </div>

        </Base>
    )
}

export default AddCategory;
