import React from 'react'
import Imagehelper from './helper/Imagehelper';


    const Card = (
       { product}, addtocart=true, removefromCart=false
    ) => {

         const showaddtoCart=addtocart=>(
            
          addtocart&& (
            <button
            onClick={() => {}}
            className="btn btn-block btn-outline-success mt-2 mb-2"
          >
            Add to Cart
          </button>
          )

         )

         const  showremoveFromCart=removefromCart=>(
           removefromCart&&(
            <button
            onClick={() => {}}
            className="btn btn-block btn-outline-danger mt-2 mb-2"
          >
            Remove from cart
          </button>
           )
         ) 


         

        return (
          <div className="card text-white bg-dark border border-info p-1 ">
            <div className="card-header lead">A photo from pexels</div>
            <div className="card-body">
              <div className="rounded border border-success p-1">
                <Imagehelper product={product}/>
              </div>
              <p className="lead bg-success font-weight-normal text-wrap">
                this photo looks great
              </p>
              <p className="btn btn-success rounded  btn-sm px-4">$ 5</p>
              <div className="row">
                <div className="col-12">
                 {showaddtoCart(addtocart)}
                </div>
                <div className="col-12">
                  {showremoveFromCart(removefromCart)}
                </div>
              </div>
            </div>
          </div>
        );
      };





export default Card;
