import React from 'react';
import { API } from '../../backend';

const Imagehelper=({product})=>{
  const imgurl =product ? `${API}/product/photo/${product._id}`:"https://images.pexels.com/photos/3561339/pexels-photo-3561339.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
    return (
        <img
        src= {imgurl}
        alt="photo"
        style={{ maxHeight: "100%", maxWidth: "100%" }}
        className="mb-3 rounded"
      />
    )
}

export default Imagehelper;
